import React from 'react'

import ReuseableBarChart from "../components/TypeReuseableBarChart"





class MonthDropDown2019 extends React.Component {

  state = {
    MonthSelected: "jan"
  }




  selectedMonth = (event) => {
    event.preventDefault()

    this.setState({MonthSelected: event.target.value})

  }

  MonthlyGraph = () => {

    switch (this.state.MonthSelected) {
        case "jan":
          return ( <div>
            <br/>
            <h2> 2019  JAN spending </h2>
            <ReuseableBarChart total={this.props.month_sum_2019.jan}
            // billsAmounts={this.props.month_categories_2019.jan.bills} 
            // subscriptionsAmounts={this.props.month_categories_2019.jan.sub} 
            // foodAmounts={this.props.month_categories_2019.jan.food} 
            // travelAmounts={this.props.month_categories_2019.jan.travel}
            // phoneAmounts={this.props.month_categories_2019.phone} 
            // holidayAmounts={this.props.month_categories_2019.jan.holiday}
            // retailAmounts={this.props.month_categories_2019.jan.retail} 
            // techAmounts={this.props.month_categories_2019.jan.tech}
            // otherAmounts={this.props.month_categories_2019.jan.other}
             />
            </div> ) 
          break;

        case"feb":
          return ( <div>
            <br/>
            <h2> 2019  FEB spending </h2>
            <ReuseableBarChart total={this.props.month_sum_2019.feb}
            // BillsAmounts={this.props.month_categories_2019.feb.bills} 
            // SubscriptionsAmounts={this.props.month_categories_2019.feb.sub} 
            // FoodAmounts={this.props.month_categories_2019.feb.food} 
            // TravelAmounts={this.props.month_categories_2019.feb.travel}
            // PhoneAmounts={this.props.month_categories_2019.phone} 
            // HolidayAmounts={this.props.month_categories_2019.feb.holiday}
            // RetailAmounts={this.props.month_categories_2019.feb.retail} 
            // TechAmounts={this.props.month_categories_2019.feb.tech}
            // OtherAmounts={this.props.month_categories_2019.feb.other}
             />
            </div> )  
          break;

        case "mar":
           return ( <div>
            <br/>
            <h2> 2019  MAR spending </h2>
            <ReuseableBarChart total={this.props.month_sum_2019.mar}
            // BillsAmounts={this.props.month_categories_2019.mar.bills} 
            // SubscriptionsAmounts={this.props.month_categories_2019.mar.sub} 
            // FoodAmounts={this.props.month_categories_2019.mar.food} 
            // TravelAmounts={this.props.month_categories_2019.mar.travel}
            // PhoneAmounts={this.props.month_categories_2019.phone} 
            // HolidayAmounts={this.props.month_categories_2019.mar.holiday}
            // RetailAmounts={this.props.month_categories_2019.mar.retail} 
            // TechAmounts={this.props.month_categories_2019.mar.tech}
            // OtherAmounts={this.props.month_categories_2019.mar.other}
             />            </div> )  
          break;

        case "apr":
          return ( <div>
            <br/>
            <h2> 2019  APR spending </h2>
            <ReuseableBarChart total={this.props.month_sum_2019.apr}
            // BillsAmounts={this.props.month_categories_2019.apr.bills} 
            // SubscriptionsAmounts={this.props.month_categories_2019.apr.sub} 
            // FoodAmounts={this.props.month_categories_2019.apr.food} 
            // TravelAmounts={this.props.month_categories_2019.apr.travel}
            // PhoneAmounts={this.props.month_categories_2019.phone} 
            // HolidayAmounts={this.props.month_categories_2019.apr.holiday}
            // RetailAmounts={this.props.month_categories_2019.apr.retail} 
            // TechAmounts={this.props.month_categories_2019.apr.tech}
            // OtherAmounts={this.props.month_categories_2019.apr.other}
             />             </div> )  
          break;

