//------------------------------------------------------------------------------------------------------------------
// IMPORTS
//------------------------------------------------------------------------------------------------------------------

import React from 'react'
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'
import { signin } from '../services/api'


//------------------------------------------------------------------------------------------------------------------
// SIGN IN FORM CLASS
//------------------------------------------------------------------------------------------------------------------

class SignInForm extends React.Component {

// inital state
//------------------------------------------------------------------------------------------------------------------  
  state = {
    username: '',
    password: ''
  }

// handle submit function (signin)
//------------------------------------------------------------------------------------------------------------------
  handleSubmit = () => {
    signin(this.state.username, this.state.password) // this signin function will return a promise
    .then( data => {
      if (data.error){
        alert(data.error)
      } else { 
        this.props.signin(data)
      }
    })
  }

// handle chaneg function 
//------------------------------------------------------------------------------------------------------------------
//change the state to the users name that has just logged in 
handleChange = event =>
    this.setState({ [event.target.name]: event.target.value })

// render component
//------------------------------------------------------------------------------------------------------------------
  render () {
    const { username, password } = this.state
    const { handleChange, handleSubmit } = this

    return (
      <div className="signInform">
        <TextField
          id='usernameInput'
          label='Username'
          value={username}
          onChange={handleChange}
          margin='normal'
          name='username'
          required
        />
        <br />
        <TextField
          id='passwordInput'
          label='Password'
          value={password}
          onChange={handleChange}
          margin='normal'
          name='password'
          type='password'
          required
        />
        <br />
        <Button onClick={handleSubmit} variant='contained' color='primary'>
          SUBMIT
        </Button>


<br/>
<br/>
<br/>
<br/>

        <img
              alt="oh no!"
              className="signInPicture"
              src="http://www.drinkingwaterfountains.co.uk/wp/wp-content/uploads/2018/04/installation-service.jpg"
            />

      </div>
    )
  }
}

//------------------------------------------------------------------------------------------------------------------
// EXPORT
//------------------------------------------------------------------------------------------------------------------


export default SignInForm
