import React, { Component } from "react";
// import ReactDOM from "react-dom";


import Dialog from '@material-ui/core/Dialog';
import API from "../services/api"
import swal from "sweetalert";




class DeletePotDashboard extends Component {

  state = { show: false };

  handleClick = () => {
    this.setState({show: !this.state.show})
  }

  deletePot = () => {

    swal({
      title: "Are you sure you want to remove this Savings Pot?",
      icon: "warning",
      buttons: true,
      dangerMode: true
      }).then(
        willDelete => {

          if (willDelete) {
            API.deleteSavingPot(this.props.item.id)
            .then( () => this.props.deleteSavingPot() )

            swal("The Saving Pot has been removed.", {
              icon: "success",
              timer: 1500
            })
          }else {
        swal("The restaurant has not been removed.", {
          timer: 1500
        });
      }
    });
  };










  render() {

    return (
        <main>
          <br/>

          <Dialog open={this.state.show} onClose={ () => {this.setState({show: false})}}>
          <h3>Are sure you want to delete?</h3>


          <button onClick={this.deletePot}>Yes</button>

          </Dialog>
          <button type="button" onClick={this.handleClick}>
            Delete
          </button>
        </main>
    );
  }
}

const container = document.createElement("div");
document.body.appendChild(container);
// ReactDOM.render(<Dashboard />, container);


export default DeletePotDashboard
